
public class MathematicalClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double e = 2.71828182846;
		
		System.out.println(Math.pow(e, e)); // e^e
		System.out.println(Math.sqrt(e)); // sqrt(e)
		System.out.println(Math.abs(-e)); // |-e|
		System.out.println(Math.random()*e); // 0<e)
		System.out.println(Math.max(e, 0)); //max of 0 and e
		System.out.println(Math.min(e, 0)); //min of 0 and e
		System.out.println(Math.round(e)); // e rounded to whole int
		System.out.println(Math.ceil(e)); // e rounded up
		System.out.println(Math.floor(e));// e rounded down
	
	}

}
